DECLARE @TableName sysname = 'DIM_PACIENTE';
DECLARE @SchemaName sysname = 'dbo';
DECLARE @PrimaryKeyColumn sysname = 'PACIENTE_SK'; -- Asume que 'ID_PACIENTE' es la PK
DECLARE @ColumnList NVARCHAR(MAX);
DECLARE @DynamicSQL NVARCHAR(MAX);

-- Paso 1: Generar la lista de columnas
SELECT @ColumnList = STUFF(
    (SELECT ' + ISNULL(CAST(' + QUOTENAME(c.name) + ' AS NVARCHAR(MAX)), '''')'
     FROM sys.columns AS c
     WHERE c.object_id = OBJECT_ID(@SchemaName + '.' + @TableName)
     ORDER BY c.column_id
     FOR XML PATH('')
    ), 1, 3, ''
);

-- Paso 2: Construir la consulta SQL
SET @DynamicSQL = N'
    SELECT
        ' + QUOTENAME(@PrimaryKeyColumn) + ' AS SK,
        CONVERT(VARCHAR(32), HASHBYTES(''MD5'', ' + @ColumnList + '), 2) AS FilaHash
    FROM ' + QUOTENAME(@SchemaName) + '.' + QUOTENAME(@TableName) + '
    WHERE fecha_actualizacion BETWEEN @fecha_inicio AND @fecha_fin
';

-- Paso 3: Asignar valores fijos a las variables de fecha
DECLARE @fecha_inicio DATETIME = '2025-08-01'; -- Reemplaza con la fecha de inicio
DECLARE @fecha_fin DATETIME = '2025-08-31';   -- Reemplaza con la fecha de fin

-- Paso 4: Ejecutar la consulta dinámica
EXEC sp_executesql
    @DynamicSQL,
    N'@fecha_inicio DATETIME, @fecha_fin DATETIME',
    @fecha_inicio = @fecha_inicio,
    @fecha_fin = @fecha_fin;

